import React from "react";
import Slider from "react-slick";
import "./Banner.scss";

const Banner = () => {
  const settings = {
    dots: true, // Hiển thị các dấu chấm điều khiển
    infinite: true, // Cho phép slide vòng lặp vô tận
    speed: 500, // Tốc độ chuyển slide
    slidesToShow: 1, // Số slide hiển thị cùng lúc
    slidesToScroll: 1, // Số slide di chuyển mỗi lần
    autoplay: true, // Tự động chuyển slide
    autoplaySpeed: 3000, // Thời gian mỗi slide trước khi tự động chuyển
  };

  return (
    <div className="banner">
      <Slider {...settings}>
        <div>
          <img
            src="./image/banner.png"
            alt="Miss Dior Banner 1"
            className="banner-image"
          />
        </div>
        <div>
          <img
            src="./image/banner.png"
            alt="Chanel No.5 Banner 2"
            className="banner-image"
          />
        </div>
        <div>
          <img
            src="./image/banner3.png"
            alt="Gucci Bloom Banner 3"
            className="banner-image"
          />
        </div>
      </Slider>
    </div>
  );
};

export default Banner;
